import React, { useEffect, useState } from "react";
import "./index.scss";
import { Carousel } from "react-carousel-minimal";
// import ModalCarousel from "../ModalCarousel";

function ModalContainer({ id, closeModal }) {
  const [dataModal, setDataModal] = useState([]);
  const [modalImg, setModalImg] = useState([]);
  useEffect(() => {
    getfetch();
  }, []);
  console.log(dataModal);
  const data  =  dataModal.map((x) => {
    return { image: x.images[0], caption: " " };
  });
  // const data = [
  //   {
  //     image:
  //       "https://upload.wikimedia.org/wikipedia/commons/thumb/0/0c/GoldenGateBridge-001.jpg/1200px-GoldenGateBridge-001.jpg",
  //     caption: "",
  //   },
  //   {
  //     image:
  //       "https://cdn.britannica.com/s:800x450,c:crop/35/204435-138-2F2B745A/Time-lapse-hyper-lapse-Isle-Skye-Scotland.jpg",
  //     caption: "",
  //   },
  //   {
  //     image:
  //       "https://static2.tripoto.com/media/filter/tst/img/735873/TripDocument/1537686560_1537686557954.jpg",
  //     caption: "",
  //   },
  //   {
  //     image:
  //       "https://upload.wikimedia.org/wikipedia/commons/thumb/1/16/Palace_of_Fine_Arts_%2816794p%29.jpg/1200px-Palace_of_Fine_Arts_%2816794p%29.jpg",
  //     caption: "",
  //   },
  //   {
  //     image:
  //       "https://i.natgeofe.com/n/f7732389-a045-402c-bf39-cb4eda39e786/scotland_travel_4x3.jpg",
  //     caption: "",
  //   },
  //   {
  //     image:
  //       "https://www.tusktravel.com/blog/wp-content/uploads/2020/07/Best-Time-to-Visit-Darjeeling-for-Honeymoon.jpg",
  //     caption: "",
  //   },
  //   {
  //     image:
  //       "https://www.omm.com/~/media/images/site/locations/san_francisco_780x520px.ashx",
  //     caption: "",
  //   },
  //   {
  //     image:
  //       "https://images.ctfassets.net/bth3mlrehms2/6Ypj2Qd3m3jQk6ygmpsNAM/61d2f8cb9f939beed918971b9bc59bcd/Scotland.jpg?w=750&h=422&fl=progressive&q=50&fm=jpg",
  //     caption: "",
  //   },
  //   {
  //     image:
  //       "https://www.oyorooms.com/travel-guide/wp-content/uploads/2019/02/summer-7.jpg",
  //     caption: "",
  //   },
  // ];
  async function getfetch() {
    const res = await fetch(
      "https://6573ac96f941bda3f2af125e.mockapi.io/juan-store/api/v1/products/" +
        id
    );
    const data = await res.json();
    console.log(data.images);
    setDataModal(data);
    setModalImg(data.images);
  }
  return (
    <div>
      <div className="modal_container">
        <i className="fa-solid fa-xmark" onClick={closeModal}></i>

        <div className="carusel">
          <Carousel
            data={data}
            time={2000}
            width="850px"
            height="500px"
            radius="10px"
            slideNumber={false}
            captionPosition="bottom"
            automatic={true}
            dots={false}
            pauseIconColor="white"
            pauseIconSize="40px"
            slideBackgroundColor="darkgrey"
            slideImageFit="cover"
            thumbnails={true}
            thumbnailWidth="100px"
            style={{
              textAlign: "center",
              maxWidth: "850px",
              maxHeight: "500px",
              margin: "40px auto",
            }}
          />
        </div>
        <div className="content">
          <div className="content_text">
            <p className="name">{dataModal.name}</p>
            <p className="price">{dataModal.price}</p>
            <p className="desc">{dataModal.description?.text}</p>
          </div>
          <div className="configurations">
            <div className="selectSize">
              <span>Size</span>
              <select name="" id="">
                <option value="">Choose an option</option>
                <option value="">Size S</option>
                <option value="">Size M</option>
                <option value="">Size L</option>
                <option value="">Size XL</option>
              </select>
            </div>
            <div className="selectColor">
              <span>Color</span>
              <select name="" id="">
                <option value="">Choose an option</option>
                <option value="">Red</option>
                <option value="">Blue</option>
                <option value="">White</option>
                <option value="">Grey </option>
              </select>
            </div>
            <div className="counterArea">
              <div className="counter">
                <div className="decrease">-</div>
                <div className="count">5</div>
                <div className="increase">+</div>
              </div>
              <button>ADD TO CART</button>
            </div>
          </div>
          <div className="icons">
            <i class="fa-regular fa-heart"></i>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ModalContainer;
