    function handleClick() {
        setIsOpen(!isOpen)
        console.log(isOpen);
        
    }
    return (
    <BasketSliderContext.Provider value={{isOpen,handleClick}}>
{children}
    </BasketSliderContext.Provider>
  )
}

export default BasketSliderProvider
